import { withStyles} from '@material-ui/core/styles';

const styles ={
  textField: {
    width:  '100%'
  }
}

class Register extends Component {
  constructor(props){
    super(props)

    this.state={
      email:'',
      login:'',
      password: '',
      password2: '',
    }
  }

  render () {
    const {classes } = this.props;
    return (
      <Paper style ={{padding:8}}>
      <form>
        <TextField
          type="email"  
          label="Email"
          className = {classes.textField}
        />
        <TextField
          label="login"
          type="text"
          className = {classes.textField}
        />
        <TextField
          label="Password"
          type="password"
          className = {classes.textField}
        />
        <TextField
          label="Password verificaiton"
          type="password"
          className = {classes.textField}
        />
        <Button variant="outlined">

        </Button>
      </form>
      </Paper>
    )
  }

}

export default withStyles( styles )(Register)
